import { useState } from "react";
import axios from "axios";

const LoginPage = () => {
  const [isRegister, setIsRegister] = useState(false);
  const [form, setForm] = useState({ name: "", email: "", password: "" });

  const toggleMode = () => setIsRegister(!isRegister);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = isRegister ? "register" : "login";

    try {
      const res = await axios.post(`/api/auth/${endpoint}`, form);
      localStorage.setItem("token", res.data.token);
      alert(`${isRegister ? "Registered" : "Logged in"} successfully!`);
    } catch (err) {
      alert(err.response?.data?.message || "Error occurred.");
    }
  };

  return (
    <div className="flex flex-col items-center mt-10">
      <h2 className="text-2xl mb-4">{isRegister ? "Register" : "Login"}</h2>
      <form className="flex flex-col gap-4 w-80" onSubmit={handleSubmit}>
        {isRegister && (
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={form.name}
            onChange={handleChange}
            required
            className="border p-2 rounded"
          />
        )}
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
          className="border p-2 rounded"
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          required
          className="border p-2 rounded"
        />
        <button type="submit" className="bg-blue-500 text-white py-2 rounded">
          {isRegister ? "Register" : "Login"}
        </button>
        <button type="button" onClick={toggleMode} className="text-sm underline">
          {isRegister ? "Already have an account? Login" : "New user? Register"}
        </button>
      </form>
    </div>
  );
};

export default LoginPage;
