// src/pages/LocationsPage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function LocationsPage() {
  const [locations, setLocations] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');

    const fetchLocations = async () => {
      try {
        const res = await axios.get('/api/locations', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setLocations(res.data);
      } catch (err) {
        setError('Failed to fetch locations. Please log in.');
      }
    };

    fetchLocations();
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold">Inventory Manager Pro</h1>
      <h2 className="text-xl font-semibold mt-2">Locations</h2>
      {error ? (
        <p className="text-red-600">{error}</p>
      ) : (
        <ul className="mt-4">
          {locations.map((loc) => (
            <li key={loc._id}>{loc.name}</li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default LocationsPage;
